js.from_to:
function convertFootPoundsToNewtonMeters(valueFrom) {
  const FTLB_TO_NM = 1.3558179483;
  if (valueFrom === null || valueFrom === undefined || isNaN(valueFrom)) return null;
  return valueFrom * FTLB_TO_NM;
}

js.to_from:
function convertNewtonMetersToFootPounds(valueTo) {
  const NM_TO_FTLB = 0.7375621493;
  if (valueTo === null || valueTo === undefined || isNaN(valueTo)) return null;
  return valueTo * NM_TO_FTLB;
}
